import styled, { createGlobalStyle, css } from 'styled-components';

export const Container = styled.div`
  width: 100vw;
	height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	padding: 20px 0;

* {
	font-family: "Roboto", sans-serif;
}
`;
